				<?php		session_start();		if(isset($_SESSION['login']))		{		$login=$_SESSION['login'];			include("pages/bd.php");			$dataquery = mys("SELECT * FROM `users` WHERE `login`='$login' ");			$user_data=mysar($dataquery);			$name= $user_data['name'];			$surname = $user_data['surname'];			$email = $user_data['email'];			?>			<h3>Вы вошли как:</h3>						<table style="font-size:12px;">			<tr><td><b>Логин:</b></td><td><?php echo $login;?> </td></tr>			<tr><td><b>Имя:</b></td><td><?php echo $name;?> </td></tr>			<tr><td><b>Фамилия:</b></td><td><?php echo $surname;?> </td></tr>			<tr><td><b>Почта:</b></td><td><?php echo $email;?> </td></tr>			</td></tr>			</table>						<?php			}			else			{			?>			<h3>Авторизация</h3>			<form action="pages/main/main_ac_login.php" method="POST">			<p>			Логин: <input type="text" name="login">			Пароль: <input type="password" name="password">			<br>			<br>			<input type="submit" value="Войти">			</p>			</form>			<?php			}			?>