<?phpinclude("../bd.php");include("../functions.php");$login=trim(htmlspecialchars(strip_tags($_POST['login'])));$password=trim(htmlspecialchars(strip_tags($_POST['password'])));$query = mys("SELECT * FROM `users` WHERE `login`='$login' ");$pass_ar = mysar($query);$stat = $pass_ar['status'];if($pass_ar['password']!=$password){	error("Неверный логин или пароль!");	die();}else{	session_start();	$_SESSION['login']=$login;	$_SESSION['status']=$stat;	header("location: ../../index.php");}?>